import React, { Component } from 'react'
import axios from 'axios'

import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default class Weather extends Component {

    //Define state default values

    constructor() {
        super();
        this.state = {
            forecast: [],
            coord: {},
            weather: [],
            main: {},
            wind: {},
            clouds: {},
            sys: {},

        };
    }

     //Component Lifecycle Callback
    componentDidMount() {
        // retrieve weather info
        axios.get(`https:api.openweathermap.org/data/2.5/weather?q=Toronto&appid=0075d6a7c0a4afa060d8299f822f958f`)
        .then(res => {
            console.log(res.data);
            // retrieve data objects
            const forecast = res.data;
            const coord = forecast.coord;
            const weather = forecast.weather[0];
            const main = forecast.main;
            const wind = forecast.wind;
            const clouds = forecast.clouds;
            const sys = forecast.sys;

            // set states
            this.setState({ forecast, coord, weather, main, wind,
                clouds, sys });
        })
            .catch(err => {
                console.error("Error : ", err)
        })
    }


    render() {

// convert unix timestamp
const getDT=(dt)=>{
      var date = new Date(dt * 1000);
      var hours = date.getHours(); 
        var min = date.getMinutes(); 

      var date = "("+date+"; "+hours+":"+min+")"
      return date;
}


var b = this.state.forecast.dt+"";
var c = parseInt(b);




// get icon image
var icon = 'http://openweathermap.org/img/wn/' + this.state.weather.icon + '@2x.png';
//console.log(icon)

var temp = parseInt(this.state.main.temp - 273.15) + "°C"
var temp_feel = parseInt(this.state.main.feels_like - 273.15) + "°C"
var temp_min = parseInt(this.state.main.temp_min - 273.15) + "°C"
var temp_max = parseInt(this.state.main.temp_max - 273.15) + "°C"

var date = new Date(this.state.sys.sunset * 1000);

        return (
            <Container className="container-fluid text-center App-header p-3">
                <div className="weather-cont p-3 text-danger">
                <h1 className="">Weather Forecast</h1>
                <h3>{this.state.forecast.name}, {this.state.sys.country}</h3>
                <p>(latitude: {this.state.coord.lat} &nbsp;
                    longitude: {this.state.coord.lon})</p>

                <p>Put date here!!!</p>
                <p>{this.state.weather.icon}</p>
                <img className="badge-pill App-logo" src = {icon} />
                <p>{this.state.weather.description}</p>
                <p>{this.state.clouds.all}%</p>
                <p>Temp: {temp}</p>
                <p>Feels Like: {temp_feel}</p>
                <p>Min: {temp_min}</p>
                <p>Max: {temp_max}</p>
                <p>{this.state.main.pressure} hPa</p>
                <p>{this.state.main.humidity}%</p>
                <p>{this.state.wind.speed}mps</p>
                <p>{this.state.wind.deg}°</p>

                <p>Convert dates here!!!</p>
                <p>{this.state.sys.sunrise}</p>
                <p>{this.state.sys.sunset}</p>



                <h5>{getDT(c)}</h5>
                </div>


            </Container>
        )
    }

}
