import React, { Component } from 'react'
import axios from 'axios'

import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';



export default class Weather extends Component {

    //Define state default values

    constructor() {
        super();
        this.state = {
            forecast: [],
            coord: {},
            weather: [],
            main: {},
            winds: {},
            clouds: {},
            sys: {},
        };
    }

     //Component Lifecycle Callback
    componentDidMount() {
        // retrieve weather info
        axios.get(`https:api.openweathermap.org/data/2.5/weather?q=Toronto&appid=0075d6a7c0a4afa060d8299f822f958f`)
        .then(res => {
            console.log(res.data);
            // retrieve data objects
            const forecast = res.data;
            const coord = forecast.coord;
            const weather = forecast.weather[0];
            const main = forecast.main;
            const winds = forecast.winds;
            const clouds = forecast.clouds;
            const sys = forecast.sys;

            // set states
            this.setState({ forecast, coord, weather, main, winds,
                clouds, sys });
        })
            .catch(err => {
                console.error("Error : ", err)
        })
    }


    render() {
        const getCurrentDate=()=>{

      var date = new Date().getDate();
      var month = new Date().getMonth() + 1;
      var year = new Date().getFullYear();

      //Alert.alert(date + '-' + month + '-' + year);
      // You can turn it in to your desired format
      return date + '-' + month + '-' + year;//format: dd-mm-yyyy;
}

        return (
            <Container className="container-fluid text-center">
                <h1 className="text-center">User List</h1>
                <h3>{this.state.forecast.name}, {this.state.sys.country}</h3>
                <h5>UTC {this.state.forecast.timezone}</h5>
                <p>(latitude: {this.state.coord.lat} &nbsp;
                    longitude: {this.state.coord.lon})</p>
                getCurrentDate()
                <h5></h5>



            </Container>
        )
    }

}
